<section class="parallax-section clearfix team_template" id="team">    <div class="mid-content">        <h1><span>Team</span></h1>        <div class="parallax-content">            <div class="page-content">                <p style="text-align: center;"><?php echo "An awesome team" ?></p>            </div>        </div>        <ul class="team-members">            <?php $user_query = get_team_members(); ?>            <?php if ( ! is_wp_error( $user_query ) && is_array( $user_query ) && ! empty( $user_query )): ?>                <?php foreach ($user_query as $user):                    // print_r($user); // for debugging                    $user_name = $user->post_title;                    $user_bio = strip_tags($user->post_content);                    // Extract image URL; return default if not available                    $user_image = get_stylesheet_directory_uri() . "/images/omnilab/default-user-male.png";                    if (array_key_exists("image", $user)) {                        $res = Array();                        if (preg_match('/src=[\"\']([^\"\']+)[\"\']/', $user->image, $res) ){                            if ( count($res) > 0 ){                                $user_image = $res[1];                            }                        }                    }                    ?>                    <li class="">                        <div class="team-member">                            <div class="team-member-image">                                <img alt="<?php echo $user_name; ?>", src="<?php echo $user_image; ?>">                            </div>                            <div class="team-member-overlay">                                <div class="item-overlay-center">                                    <h2><?php echo $user_name; ?></h2>                                    <p><?php echo extract_sentences($user_bio, 2); ?></p>                                    <ul class="team-social-icon">                                        <?php if ( array_key_exists("url", $user) && $user->url != "" ):?>                                            <li>                                                <a title="Website" href="<?php echo $user->url; ?>">                                                    <i class="fa fa-home fa-lg"></i>                                                </a>                                            </li>                                        <?php endif ?>                                        <?php if ( array_key_exists("github", $user) && $user->github != "" ):?>                                            <li>                                                <a title="Github" href="https://github.com/<?php echo $user->github; ?>">                                                    <i class="fa fa-github fa-lg"></i>                                                </a>                                            </li>                                        <?php endif ?>                                        <?php if ( array_key_exists("twitter", $user) && $user->twitter != "" ):?>                                            <li>                                                <a title="Twitter" href="https://twitter.com/<?php echo $user->twitter; ?>">                                                    <i class="fa fa-twitter fa-lg"></i>                                                </a>                                            </li>                                        <?php endif ?>                                        <?php if ( array_key_exists("weixin", $user) && $user->weixin != "" ):?>                                            <li>                                                <a title="Weixin" href="https://wx.qq.com/#<?php echo $user->weixin; ?>">                                                    <i class="fa fa-weixin fa-lg"></i>                                                </a>                                            </li>                                        <?php endif ?>                                        <?php if ( array_key_exists("weibo", $user) && $user->weibo != "" ):?>                                            <li>                                                <a title="Weibo" href="http://weibo.com/<?php echo $user->weibo; ?>">                                                    <i class="fa fa-weibo fa-lg"></i>                                                </a>                                            </li>                                        <?php endif ?>                                        <?php if ( array_key_exists("google", $user) && $user->google != "" ):?>                                            <li>                                                <a title="Google+"" href="http://plus.google.com/<?php echo $user->google; ?>">                                                    <i class="fa fa-google-plus fa-lg"></i>                                                </a>                                            </li>                                        <?php endif ?>                                        <?php if ( array_key_exists("linkedin", $user) && $user->linkedin != "" ):?>                                            <li>                                                <a title="LinkedIn"" href="http://www.linkedin.com/in/<?php echo $user->linkedin; ?>">                                                    <i class="fa fa-linkedin-square fa-lg"></i>                                                </a>                                            </li>                                        <?php endif ?>                                        <?php if ( array_key_exists("contact_email", $user) && $user->contact_email != "" ):?>                                            <li>                                                <a title="Email" href="mailto:<?php echo $user->contact_email; ?>">                                                    <i class="fa fa-paper-plane-o fa-lg"></i>                                                </a>                                            </li>                                        <?php endif ?>                                    </ul>                                </div>                            </div>                        </div>                    </li>                <?php endforeach ?>            <?php endif ?>        </ul>    </div></section>